import React from 'react';

const Blog = () => {
  const articles = [
    {
      title: "Financial Discipline: The Path to Freedom",
      excerpt: "Learn how to maintain discipline in your financial life to achieve long-term goals.",
      content: "Maintaining financial discipline is crucial for achieving financial freedom. It involves consistent saving, smart spending, and investing wisely. In this article, we explore practical tips to stay on track with your budget and goals."
    },
    {
      title: "Understanding Investments: T-Bills vs MMFs",
      excerpt: "A beginner's guide to safe investment options in Kenya.",
      content: "T-Bills and MMFs are popular low-risk investments. T-Bills are government securities with fixed returns, while MMFs offer liquidity and slightly higher yields. Choose based on your liquidity needs and risk tolerance."
    },
    {
      title: "The Importance of Emergency Funds",
      excerpt: "Why every financial plan needs an emergency fund and how to build one.",
      content: "An emergency fund is your safety net for unexpected expenses like medical emergencies or job loss. Aim for 3-6 months of living expenses in a liquid account to avoid debt during tough times."
    }
  ];

  return (
    <div className="p-4 md:p-8 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Financial Education Blog</h1>
      
      <div className="space-y-8">
        {articles.map((article, index) => (
          <div key={index} className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold mb-2">{article.title}</h2>
            <p className="text-gray-600 mb-4 italic">{article.excerpt}</p>
            <p>{article.content}</p>
            <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
              Read More
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
