import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

const Register = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [userId, setUserId] = useState(null);
  const [form, setForm] = useState({
    email: '', password: '',
    firstName: '', middleName: '', lastName: '', dob: '', phone: '', city: '',
    address: '', termsAccepted: false, avatar: null,
    kycSkipped: false
  });
  const [submitting, setSubmitting] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleAvatarChange = (e) => {
    setForm(prev => ({ ...prev, avatar: e.target.files[0] }));
  };

  const validateStep = (step) => {
    const newErrors = {};

    if (step === 1) {
      if (!form.email) newErrors.email = 'Email is required';
      if (!form.password) newErrors.password = 'Password is required';
      else if (form.password.length < 8) newErrors.password = 'Password must be at least 8 characters';
    } else if (step === 2) {
      if (!form.firstName) newErrors.firstName = 'First name is required';
      if (!form.lastName) newErrors.lastName = 'Last name is required';
      if (!form.dob) newErrors.dob = 'Date of birth is required';
      if (!form.phone) newErrors.phone = 'Phone number is required';
      if (!form.city) newErrors.city = 'City is required';
    } else if (step === 3) {
      if (!form.termsAccepted) newErrors.termsAccepted = 'You must accept the terms';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleStepSubmit = async (e) => {
    e.preventDefault();
    if (!validateStep(currentStep)) return;

    setSubmitting(true);
    try {
      let response;
      if (currentStep === 1) {
        response = await api.post('/api/auth/register/step1', {
          email: form.email,
          password: form.password
        });
        setUserId(response.data.userId);
        setCurrentStep(2);
      } else if (currentStep === 2) {
        response = await api.patch(`/api/auth/register/${userId}/step2`, {
          firstName: form.firstName,
          middleName: form.middleName,
          lastName: form.lastName,
          dob: form.dob,
          phone: form.phone,
          city: form.city
        });
        setCurrentStep(3);
      } else if (currentStep === 3) {
        const formData = new FormData();
        formData.append('userId', userId);
        formData.append('address', form.address);
        formData.append('termsAccepted', form.termsAccepted);
        if (form.avatar) {
          formData.append('avatar', form.avatar);
        }

        response = await api.patch(`/api/auth/register/${userId}/step3`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        setCurrentStep(4);
      } else if (currentStep === 4) {
        response = await api.patch(`/api/auth/register/${userId}/step4`, {
          userId,
          kycSkipped: form.kycSkipped
        });

        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
          navigate('/dashboard');
        }
      }
    } catch (err) {
      const errorMessage = err?.response?.data?.message || 'An error occurred';
      setErrors({ general: errorMessage });
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="p-8 min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-center">Create Account</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium mb-2">Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Your name"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium mb-2">Email</label>
            <input
              type="email"
              name="email"
              value={form.email}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="you@example.com"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium mb-2">Date of Birth</label>
            <input
              type="date"
              name="dob"
              value={form.dob}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium mb-2">Phone Number</label>
            <input
              type="tel"
              name="phone"
              value={form.phone}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="+254..."
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium mb-2">City</label>
            <input
              type="text"
              name="city"
              value={form.city}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Nairobi"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-medium mb-2">Password</label>
            <input
              type="password"
              name="password"
              value={form.password}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="••••••••"
              required
              minLength={6}
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-medium mb-2">Profile Picture (optional)</label>
            <input
              type="file"
              name="avatar"
              onChange={handleAvatarChange}
              accept="image/*"
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <button
            type="submit"
            disabled={submitting}
            className={`w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 ${submitting ? 'opacity-50 cursor-not-allowed' : ''}`}
          >
            {submitting ? 'Creating...' : 'Register'}
          </button>
        </form>
        <p className="mt-4 text-center text-sm">
          Already have an account?{' '}
          <Link to="/login" className="text-blue-600 hover:underline">Login</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
