import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import axios from 'axios';

ChartJS.register(ArcElement, Tooltip, Legend);

const Dashboard = () => {
  const [data, setData] = useState({ netWorth: 0, allocationData: [], healthScore: 0 });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    axios.get('/api/reports/dashboard', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setData(response.data);
      setLoading(false);
    })
    .catch(error => {
      console.error('Dashboard data error:', error);
      if (error.response.status === 401) navigate('/login');
      setLoading(false);
    });
  }, [navigate]);

  if (loading) return <div className="p-8">Loading dashboard...</div>;

  const pieData = {
    labels: data.allocationData.map(acc => acc.type),
    datasets: [{
      data: data.allocationData.map(acc => acc.balance),
      backgroundColor: data.allocationData.map(acc => acc.status === 'red' ? '#ef4444' : acc.status === 'green' ? '#10b981' : '#3b82f6'),
    }]
  };

  return (
    <div className="p-4 md:p-8 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Dashboard</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-2">Net Worth</h2>
          <p className="text-2xl text-green-600">KES {data.netWorth.toFixed(2)}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-2">Total Balance</h2>
          <p className="text-2xl text-blue-600">KES {data.totalBalance.toFixed(2)}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-2">Health Score</h2>
          <p className="text-2xl text-purple-600">{data.healthScore}%</p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Allocation Compliance</h2>
          <Pie data={pieData} />
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Quick Actions</h2>
          <div className="space-y-2">
            <button className="w-full p-2 bg-blue-500 text-white rounded hover:bg-blue-600">Add Income</button>
            <button className="w-full p-2 bg-green-500 text-white rounded hover:bg-green-600">View Reports</button>
            <button className="w-full p-2 bg-purple-500 text-white rounded hover:bg-purple-600">Manage Investments</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
