import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../services/api';
import PasswordInput from '../components/PasswordInput';

const Register = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [userId, setUserId] = useState(null);
  const [form, setForm] = useState({
    email: '', password: '',
    firstName: '', middleName: '', lastName: '', dob: '', phone: '', city: '', idNumber: '',
    address: '', termsAccepted: false, avatar: null,
    kycSkipped: false
  });
  const [submitting, setSubmitting] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleAvatarChange = (e) => {
    setForm(prev => ({ ...prev, avatar: e.target.files[0] }));
  };

  const validateStep = (step) => {
    const newErrors = {};

    if (step === 1) {
      if (!form.email) newErrors.email = 'Email is required';
      if (!form.password) newErrors.password = 'Password is required';
      else if (form.password.length < 8) newErrors.password = 'Password must be at least 8 characters';
    } else if (step === 2) {
      if (!form.firstName) newErrors.firstName = 'First name is required';
      if (!form.lastName) newErrors.lastName = 'Last name is required';
      if (!form.dob) newErrors.dob = 'Date of birth is required';
      if (!form.phone) newErrors.phone = 'Phone number is required';
      if (!form.city) newErrors.city = 'City is required';
    } else if (step === 3) {
      if (!form.termsAccepted) newErrors.termsAccepted = 'You must accept the terms';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleStepSubmit = async (e) => {
    e.preventDefault();
    if (!validateStep(currentStep)) return;

    setSubmitting(true);
    try {
      let response;
      if (currentStep === 1) {
        response = await api.post('/api/auth/register/step1', {
          email: form.email,
          password: form.password
        });
        setUserId(response.data.userId);
        setCurrentStep(2);
      } else if (currentStep === 2) {
        response = await api.patch(`/api/auth/register/${userId}/step2`, {
          firstName: form.firstName,
          middleName: form.middleName,
          lastName: form.lastName,
          dob: form.dob,
          phone: form.phone,
          city: form.city
        });
        setCurrentStep(3);
      } else if (currentStep === 3) {
        const formData = new FormData();
        formData.append('userId', userId);
        formData.append('address', form.address);
        formData.append('termsAccepted', form.termsAccepted);
        if (form.avatar) {
          formData.append('avatar', form.avatar);
        }

        response = await api.patch(`/api/auth/register/${userId}/step3`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        setCurrentStep(4);
      } else if (currentStep === 4) {
        response = await api.patch(`/api/auth/register/${userId}/step4`, {
          userId,
          kycSkipped: form.kycSkipped
        });

        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
          navigate('/dashboard');
        }
      }
    } catch (err) {
      const errorMessage = err?.response?.data?.message || 'An error occurred';
      setErrors({ general: errorMessage });
    } finally {
      setSubmitting(false);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Account Information</h3>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">Email</label>
              <input
                type="email"
                name="email"
                value={form.email}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.email ? 'border-red-500' : ''}`}
                placeholder="you@example.com"
                required
              />
              {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">Password</label>
              <input
                type="password"
                name="password"
                value={form.password}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.password ? 'border-red-500' : ''}`}
                placeholder="••••••••"
                required
              />
              {errors.password && <p className="text-red-500 text-xs mt-1">{errors.password}</p>}
            </div>
          </>
        );
      case 2:
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Personal Information</h3>
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-gray-700 text-sm font-medium mb-2">First Name</label>
                <input
                  type="text"
                  name="firstName"
                  value={form.firstName}
                  onChange={handleChange}
                  className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.firstName ? 'border-red-500' : ''}`}
                  required
                />
                {errors.firstName && <p className="text-red-500 text-xs mt-1">{errors.firstName}</p>}
              </div>
              <div>
                <label className="block text-gray-700 text-sm font-medium mb-2">Middle Name (optional)</label>
                <input
                  type="text"
                  name="middleName"
                  value={form.middleName}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">Last Name</label>
              <input
                type="text"
                name="lastName"
                value={form.lastName}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.lastName ? 'border-red-500' : ''}`}
                required
              />
              {errors.lastName && <p className="text-red-500 text-xs mt-1">{errors.lastName}</p>}
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">Date of Birth</label>
              <input
                type="date"
                name="dob"
                value={form.dob}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.dob ? 'border-red-500' : ''}`}
                required
              />
              {errors.dob && <p className="text-red-500 text-xs mt-1">{errors.dob}</p>}
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">Phone Number</label>
              <input
                type="tel"
                name="phone"
                value={form.phone}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.phone ? 'border-red-500' : ''}`}
                placeholder="+254..."
                required
              />
              {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">City</label>
              <input
                type="text"
                name="city"
                value={form.city}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 ${errors.city ? 'border-red-500' : ''}`}
                placeholder="Nairobi"
                required
              />
              {errors.city && <p className="text-red-500 text-xs mt-1">{errors.city}</p>}
            </div>
          </>
        );
      case 3:
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Additional Information</h3>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">Address (optional)</label>
              <textarea
                name="address"
                value={form.address}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="3"
                placeholder="Your address..."
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2">Profile Picture (optional)</label>
              <input
                type="file"
                name="avatar"
                onChange={handleAvatarChange}
                accept="image/*"
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div className="mb-4">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  name="termsAccepted"
                  checked={form.termsAccepted}
                  onChange={handleChange}
                  className="mr-2"
                  required
                />
                <span className="text-sm">I accept the <a href="#" className="text-blue-600 hover:underline">Terms of Service</a> and <a href="#" className="text-blue-600 hover:underline">Privacy Policy</a></span>
              </label>
              {errors.termsAccepted && <p className="text-red-500 text-xs mt-1">{errors.termsAccepted}</p>}
            </div>
          </>
        );
      case 4:
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Identity Verification</h3>
            <div className="mb-4 p-4 bg-blue-50 rounded-lg">
              <p className="text-sm text-gray-700 mb-3">
                For enhanced security and access to all features, we recommend completing identity verification.
                This helps us protect your account and comply with financial regulations.
              </p>
              <div className="flex items-center mb-3">
                <input
                  type="checkbox"
                  name="kycSkipped"
                  checked={form.kycSkipped}
                  onChange={handleChange}
                  className="mr-2"
                />
                <span className="text-sm">Skip verification for now (you can complete this later)</span>
              </div>
              {!form.kycSkipped && (
                <div className="text-sm text-gray-600">
                  <p>Verification typically includes:</p>
                  <ul className="list-disc ml-5 mt-1">
                    <li>ID document upload</li>
                    <li>Address verification</li>
                    <li>Basic background check</li>
                  </ul>
                </div>
              )}
            </div>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Create your Vault5 account
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Step {currentStep} of 4
          </p>
          <div className="mt-4 w-full bg-gray-200 rounded-full h-2">
            <div
              className="bg-blue-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${(currentStep / 4) * 100}%` }}
            ></div>
          </div>
        </div>

        <div className="bg-white py-8 px-6 shadow rounded-lg sm:px-10">
          {errors.general && (
            <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
              {errors.general}
            </div>
          )}

          <form onSubmit={handleStepSubmit} className="space-y-6">
            {renderStepContent()}

            <div className="flex space-x-4">
              {currentStep > 1 && (
                <button
                  type="button"
                  onClick={() => setCurrentStep(currentStep - 1)}
                  className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded-lg hover:bg-gray-400 transition duration-200"
                >
                  Back
                </button>
              )}
              <button
                type="submit"
                disabled={submitting}
                className={`flex-1 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition duration-200 ${submitting ? 'opacity-50 cursor-not-allowed' : ''}`}
              >
                {submitting ? 'Processing...' : currentStep === 4 ? 'Complete Registration' : 'Continue'}
              </button>
            </div>
          </form>

          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600">
              Already have an account?{' '}
              <Link to="/login" className="font-medium text-blue-600 hover:text-blue-500">
                Sign in
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
