const Dashboard = () => {
  const [data, setData] = useState({ netWorth: 0, allocationData: [], healthScore: 0, totalBalance: 0 });
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchData = async () => {
      try {
        const [dashboardRes, accountsRes] = await Promise.all([
          api.get('/api/reports/dashboard'),
          api.get('/api/accounts')
        ]);

        setData(dashboardRes.data);
        setAccounts(accountsRes.data);
        setLoading(false);
      } catch (error) {
        console.error('Dashboard data error:', error);
        if (error.response?.status === 401) navigate('/login');
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  if (loading) return <div className="p-8">Loading dashboard...</div>;

  const pieData = {
    labels: data.allocationData.map(acc => acc.type),
    datasets: [{
      data: data.allocationData.map(acc => acc.balance),
      backgroundColor: data.allocationData.map(acc => acc.status === 'red' ? '#ef4444' : acc.status === 'green' ? '#10b981' : '#3b82f6'),
    }]
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'red': return '#E53935'; // debt
      case 'green': return '#43A047'; // on-target
      case 'blue': return '#1E88E5'; // surplus
      default: return '#9E9E9E';
    }
  };

  const getProgressPercentage = (account) => {
    // Show allocation compliance % or goal progress % if goal exists
    if (account.target > 0) {
      return Math.min((account.balance / account.target) * 100, 100);
    }
    // For now, show allocation compliance as percentage toward target allocation
    return Math.min((account.balance / (data.totalBalance * account.percentage / 100)) * 100, 100);
  };

  const handleAddIncome = () => {
    // TODO: Implement add income modal
    alert('Add Income feature coming soon!');
  };

  const handleViewReports = () => {
    navigate('/reports');
  };

  const handleManageInvestments = () => {
    navigate('/investments');
  };

  return (
    <div className="p-4 md:p-8 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Dashboard</h1>

      {/* Summary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-2">Net Worth</h2>
          <p className="text-2xl text-green-600">KES {data.netWorth?.toFixed(2) || '0.00'}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-2">Total Balance</h2>
          <p className="text-2xl text-blue-600">KES {data.totalBalance?.toFixed(2) || '0.00'}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-2">Health Score</h2>
          <p className="text-2xl text-purple-600">{data.healthScore || 0}%</p>
        </div>
      </div>

      {/* 6 Account Cards */}
      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-6">Your Vault Accounts</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {accounts.map((account) => (
            <div key={account._id} className="bg-white p-6 rounded-lg shadow border-l-4" style={{ borderLeftColor: getStatusColor(account.status) }}>
              <div className="flex justify-between items-start mb-4">
                <h3 className="text-lg font-semibold text-gray-800">{account.type}</h3>
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                  account.status === 'green' ? 'bg-green-100 text-green-800' :
                  account.status === 'red' ? 'bg-red-100 text-red-800' :
                  'bg-blue-100 text-blue-800'
                }`}>
                  {account.status === 'green' ? 'On Target' :
                   account.status === 'red' ? 'Behind' : 'Surplus'}
                </span>
              </div>

              <div className="mb-4">
                <p className="text-2xl font-bold text-gray-900">KES {account.balance?.toFixed(2) || '0.00'}</p>
                <p className="text-sm text-gray-600">Target: {account.percentage}%</p>
              </div>

              <div className="mb-4">
                <div className="flex justify-between text-sm text-gray-600 mb-1">
                  <span>Progress</span>
                  <span>{getProgressPercentage(account).toFixed(0)}%</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="h-2 rounded-full transition-all duration-300"
                    style={{
                      width: `${getProgressPercentage(account)}%`,
                      backgroundColor: getStatusColor(account.status)
                    }}
                  ></div>
                </div>
              </div>

              {account.balance === 0 && (
                <p className="text-sm text-gray-500">No funds allocated yet. Add income to get started.</p>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Charts and Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Allocation Compliance</h2>
          {data.allocationData && data.allocationData.length > 0 ? (
            <Pie data={pieData} />
          ) : (
            <p className="text-gray-500">No allocation data available</p>
          )}
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Quick Actions</h2>
          <div className="space-y-3">
            <button
              onClick={handleAddIncome}
              className="w-full p-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-200 font-medium"
            >
              Add Income
            </button>
            <button
              onClick={handleViewReports}
              className="w-full p-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-200 font-medium"
            >
              View Reports
            </button>
            <button
              onClick={handleManageInvestments}
              className="w-full p-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-200 font-medium"
            >
              Manage Investments
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
