import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const Lending = () => {
  const [lendings, setLendings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [form, setForm] = useState({ borrowerName: '', borrowerContact: '', amount: '', type: 'non-emergency', expectedReturnDate: '', notes: '', approvedEmergency: false });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    setLoading(true);
    api.get('/api/lending')
    .then(response => {
      setLendings(response.data);
      setLoading(false);
    })
    .catch(error => {
      console.error('Lending data error:', error);
      if (error.response && error.response.status === 401) navigate('/login');
      setLoading(false);
    });
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    api.post('/api/lending', form)
    .then(response => {
      setLendings([response.data, ...lendings]);
      setShowForm(false);
      setForm({ borrowerName: '', borrowerContact: '', amount: '', type: 'non-emergency', expectedReturnDate: '', notes: '', approvedEmergency: false });
      alert('Lending request created successfully');
    })
    .catch(error => {
      console.error('Create lending error:', error);
      alert(error.response?.data?.message || 'Error creating lending request');
    });
  };

  const updateStatus = (id, status) => {
    const token = localStorage.getItem('token');
    const actualReturnDate = status === 'repaid' ? new Date().toISOString().split('T')[0] : '';
    api.put(`/api/lending/${id}/status`, { status, actualReturnDate })
    .then(response => {
      setLendings(lendings.map(l => l._id === id ? response.data : l));
      alert('Status updated successfully');
    })
    .catch(error => {
      console.error('Update status error:', error);
      alert(error.response?.data?.message || 'Error updating status');
    });
  };

  if (loading) return <div className="p-8">Loading lendings...</div>;

  return (
    <div className="p-4 md:p-8 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Lending</h1>

      <button onClick={() => setShowForm(!showForm)} className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 mb-8">
        {showForm ? 'Cancel' : 'New Lending Request'}
      </button>

      {showForm && (
        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
          <h2 className="text-xl font-semibold mb-4">New Lending Request</h2>
          <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input 
              type="text" 
              name="borrowerName" 
              placeholder="Borrower Name" 
              value={form.borrowerName} 
              onChange={handleChange}
              className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required 
            />
            <input 
              type="tel" 
              name="borrowerContact" 
              placeholder="Borrower Contact (optional)" 
              value={form.borrowerContact} 
              onChange={handleChange}
              className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input 
              type="number" 
              name="amount" 
              placeholder="Amount" 
              value={form.amount} 
              onChange={handleChange}
              className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required 
            />
            <select 
              name="type" 
              value={form.type} 
              onChange={handleChange}
              className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="non-emergency">Non-Emergency (Favor)</option>
              <option value="emergency">Emergency (Family Medical, Urgent)</option>
            </select>
            <input 
              type="date" 
              name="expectedReturnDate" 
              value={form.expectedReturnDate} 
              onChange={handleChange}
              className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required 
            />
            <textarea 
              name="notes" 
              placeholder="Notes" 
              value={form.notes} 
              onChange={handleChange}
              className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 md:col-span-2"
              rows="3"
            />
            <div className="md:col-span-2">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  name="approvedEmergency"
                  checked={form.approvedEmergency}
                  onChange={(e) => setForm(prev => ({ ...prev, approvedEmergency: e.target.checked }))}
                  className="mr-2 leading-tight"
                />
                <span className="text-sm font-medium">Allow use of Emergency/Long-Term funds (risky)</span>
              </label>
            </div>
            <button type="submit" className="md:col-span-2 bg-green-500 text-white py-2 rounded-lg hover:bg-green-600">
              Create Request
            </button>
          </form>
        </div>
      )}

      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold mb-4">Lending History</h2>
        <div className="space-y-4">
          {lendings.map(lending => (
            <div key={lending._id} className={`border p-4 rounded-lg ${lending.status === 'repaid' ? 'border-green-300' : lending.status === 'overdue' ? 'border-red-300' : 'border-gray-300'}`}>
              <div className="font-medium">{lending.borrowerName}</div>
              <div>Type: {lending.type}</div>
              <div>Amount: KES {lending.amount.toFixed(2)}</div>
              <div>Expected Return: {new Date(lending.expectedReturnDate).toLocaleDateString()}</div>
              <div>Status: <span className={`capitalize px-2 py-1 rounded text-sm ${lending.status === 'repaid' ? 'bg-green-100 text-green-800' : lending.status === 'overdue' ? 'bg-red-100 text-red-800' : 'bg-gray-100 text-gray-800'}`}>{lending.status}</span></div>
              {lending.sourceAccounts && lending.sourceAccounts.length > 0 && (
                <div className="text-xs text-gray-600">Sources: {lending.sourceAccounts.map(s => `${s.account} KES ${s.amount.toFixed(2)}`).join(', ')}</div>
              )}
              <div className="mt-2 space-x-2">
                <select 
                  onChange={(e) => updateStatus(lending._id, e.target.value)} 
                  className="p-1 border rounded text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="pending">Pending</option>
                  <option value="repaid">Mark as Repaid</option>
                  <option value="written_off">Write Off as Gift</option>
                  <option value="overdue">Mark as Overdue</option>
                </select>
              </div>
            </div>
          ))}
        </div>
        {lendings.length === 0 && <p>No lending history.</p>}
      </div>
    </div>
  );
};

export default Lending;
