import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState({ name: '', avatar: '', email: '' });
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    setLoading(true);
    axios.get('/api/auth/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setUser({
        name: response.data.name,
        avatar: response.data.avatar,
        email: response.data.email
      });
      setLoading(false);
    })
    .catch(error => {
      console.error('Profile error:', error);
      if (error.response && error.response.status === 401) navigate('/login');
      setLoading(false);
    });
  }, [navigate]);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    setUpdating(true);
    axios.put('/api/auth/profile', user, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setUser({
        name: response.data.name,
        avatar: response.data.avatar,
        email: response.data.email
      });
      alert('Profile updated successfully');
      setUpdating(false);
    })
    .catch(error => {
      console.error('Update profile error:', error);
      alert(error.response?.data?.message || 'Error updating profile');
      setUpdating(false);
    });
  };

  if (loading) return <div className="p-8">Loading profile...</div>;

  return (
    <div className="p-4 md:p-8 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Profile</h1>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Name</label>
            <input 
              type="text" 
              name="name" 
              value={user.name} 
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required 
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-1">Email</label>
            <input 
              type="email" 
              name="email" 
              value={user.email} 
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required 
            />
          </div>
          <div className="mb-6">
            <label className="block text-sm font-medium mb-1">Avatar URL</label>
            <input 
              type="url" 
              name="avatar" 
              value={user.avatar} 
              onChange={handleChange}
              className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <button 
            type="submit" 
            disabled={updating}
            className={`w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 disabled:bg-gray-400 ${updating ? 'opacity-50 cursor-not-allowed' : ''}`}
          >
            {updating ? 'Updating...' : 'Update Profile'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Profile;
